export declare const styles = ".nexai-chat-bubble {\n  display: flex;\n}\n/* Hide scrollbar for Chrome, Safari and Opera */\n.bubble-thread-box::-webkit-scrollbar {\n  display: none;\n}\n.bubble-thread-box {\n  -ms-overflow-style: none;  \n  scrollbar-width: none;\n} \n\n.bubble-thread-box {\n  max-height: calc(100vh - 182px);\n  /* mask-image: linear-gradient(to top,white 60%, transparent); */\n}\n\n.nexai-chat-bubble .chat-thread:first-child {\n  margin-top: 0;\n}\n\n/* .nexai-chat-bubble .chat-thread {\n  background-color: #f1f5f9;\n}\n\n.nexai-chat-bubble .chat-thread.thread-user-bot {\n  background-color: #fff;\n}\n\n.nexai-chat-bubble .chat-thread .tri-left.left-bottom:before{\n\tborder-right-color: #f1f5f9;\n}\n\n.nexai-chat-bubble .chat-thread.thread-user-bot .tri-left.left-bottom:before{\n\tborder-right-color: #fff;\n} */\n\n.tri-left {\n  position: absolute;\n  bottom: 0;\n  left: 0;\n}\n.tri-left.left-bottom:before{\n\tcontent: '';\n\tposition: absolute;\n\tleft: -1px;\n\tbottom: 12px;\n\twidth: 0;\n\theight: 0;\n\tborder: 18px solid transparent;\n\tborder-right-color: #fff;\n\tborder-left: 0;\n\tmargin-top: -18px;\n\tmargin-left: -18px;\n  z-index: 1000;\n}\n.tri-left.left-bottom:after{\n\tcontent: '';\n\tposition: absolute;\n\tleft: -1px;\n\tbottom: 10px;\n\twidth: 0;\n\theight: 0;\n\tborder: 20px solid transparent;\n\tborder-right-color: #e2e8f0;\n\tborder-left: 0;\n\tmargin-top: -20px;\n\tmargin-left: -20px;\n}\n\n.nexai-chat-bubble code {\n  padding: 5px 8px 5px 8px;\n  border: 1px solid #e2e8f0;\n  box-shadow: 0 0 5px #e2e8f0;\n  margin: 5px;\n  background: #f7f9fb;\n  display: block;\n  border-radius: 8px;\n  text-overflow: ellipsis;\n  overflow-x: hidden;\n  overflow-y: auto;\n  max-height: 500px;\n  font-size: 12px;\n}\n\n.nexai-chat-bubble .prism-code {\n  padding: 5px 8px 5px 8px;\n  box-shadow: 0 0 5px #e2e8f0;\n  margin: 5px;\n  display: block;\n  border-radius: 8px;\n  text-overflow: ellipsis;\n  overflow-x: hidden;\n  overflow-y: auto;\n  max-height: 500px;\n  font-size: 12px;\n}\n\n#nexai-audio-wave {\n  width: 80px;\n  height: 80px;\n}\n\n.typing-busy-indicator {\n  display: inline-block;\n}\n\n.typing-text {\n  display: inline-block;\n  vertical-align: middle;\n  animation: blink 1s infinite;\n}\n\n@keyframes blink {\n  0% {\n    opacity: 0.2;\n  }\n  20% {\n    opacity: 1;\n  }\n  100% {\n    opacity: 0.2;\n  }\n}\n\n.dot-1, .dot-2, .dot-3 {\n  display: inline-block;\n  width: 4px;\n  height: 4px;\n  background-color: #fff;\n  border-radius: 50%;\n  margin-left: 4px;\n  animation: dot-animation 1.5s infinite;\n}\n\n.dot-2 {\n  animation-delay: 0.5s;\n}\n\n.dot-3 {\n  animation-delay: 1s;\n}\n\n@keyframes dot-animation {\n  0%, 100% {\n    transform: translateY(0);\n  }\n  50% {\n    transform: translateY(3px);\n  }\n}\n\n.code-block .code-copy {\n  opacity: 0;\n  pointer-events: none;\n  transition: opacity 0.3s;\n}\n\n.code-block:hover .code-copy {\n  opacity: 1;\n  pointer-events: all;\n}\n";
